mapper.select:
SELECT ATT_NO, ATT_CHANGE, ATT_POST, ATT_ORIGIN
FROM TB_ATTACHMENT
WHERE ATT_POST = #{attPost}

mapper.update:
UPDATE TB_POST
SET POST_STATUS = 'N'
WHERE POST_NO= #{postNo}

UPDATE TB_REPLY
SET STATUS = 'N'
WHERE REPLY_NO= #{replyNo}

UPDATE TB_POST
SET POST_TITLE= #{postTitle}
, POST_CONTENT= #{postContent}
WHERE POST_NO= #{postNo}

UPDATE TB_ATTACHMENT
SET ATT_ORIGIN = #{attOrigin}
, ATT_CHANGE = #{attChange}
, ATT_NOTICE = #{attNotice}
, ATT_POST = #{attPost}
, ATT_CATCH = #{attCatch}
WHERE ATT_NO = #{attNo}

mapper.select:
SELECT COUNT(*)
FROM TB_POST
WHERE POST_STATUS='Y' AND POST_WRITER = #{userNo}

SELECT COUNT(*)
FROM TB_CATCH
WHERE CATCH_STATUS='Y' AND CATCH_WRITER = #{userNo}

SELECT COUNT(*)
FROM TB_QA
WHERE Q_WRITER = #{userNo}

SELECT ATT_NO, ATT_CHANGE, ATT_CATCH, ATT_ORIGIN
FROM TB_ATTACHMENT
WHERE ATT_CATCH = #{attCatch}

mapper.update:
UPDATE TB_CATCH
SET CATCH_STATUS = 'N'
WHERE CATCH_NO= #{catchNo}

UPDATE TB_CATCH
SET CATCH_TITLE= #{catchTitle}
, CATCH_CONTENT= #{catchContent}
WHERE CATCH_NO= #{catchNo}

UPDATE TB_ATTACHMENT
SET ATT_ORIGIN = #{attOrigin}
, ATT_CHANGE = #{attChange}
, ATT_NOTICE = #{attNotice}
, ATT_POST = #{attPost}
, ATT_CATCH = #{attCatch}
WHERE ATT_NO = #{attNo}

mapper.select:
SELECT COUNT(*)
FROM TB_LIKE
WHERE CATCH_NO = #{catchNo} AND LIKE_USER = #{likeUser}

SELECT COUNT(*)
FROM TB_LIKE
WHERE CATCH_NO = #{catchNo}

mapper.update:
UPDATE TB_QA
SET Q_TITLE = #{qaTitle}, Q_CONTENT = #{qaContent}
WHERE QA_NO = #{qaNo}
